import requestsfrom bs4 import BeautifulSoupname = input()url = 'https://www.citilink.ru/catalog/videokarty/'r = requests.get(url)soup = BeautifulSoup(r.text , 'html.parser')url_cart = []for i in soup.find_all('a'):    try:        if '/product/videokarta' in i.get('href'):            if 'www.citilink.ru/' not in i.get('href'):                url_cart.append(f"www.citilink.ru{i.get('href')}")            else:                url_cart.append(i.get('href'))    except:        continueprint([i for i in url_cart if name in i ])